/* You are given a string. You need to return True if the string is a palindrome, False otherwise
    palindrome_check("radar") => True
    palindrome_check("hello") => False */
const palindromeCheck = (a) => {
    let start = 0;
    let end = a.length - 1;
    while (start < end) {
        if (a[start] !== a[end]) {
            return false;
        }
        start++;
        end--;
    }
    return true;
}; // O(n) time complexity where n is the length of the string. O(1) space complexity. 

