const nextSmallestAlt = (lst) => {
  if (!lst || lst.length < 2) return null;
  let min = Infinity;
  let secondMin = Infinity;
  for (let num of lst) {
    if (num < min) {
      secondMin = min;
      min = num;
    } else if (num < secondMin && num !== min) {
      secondMin = num;
    }
  }
  return secondMin === Infinity ? null : secondMin;
}; // O(n) time complexity

