def merge(a, b):
    result = {}
    for key in a:
        if key.isalpha():
            if isinstance(a[key], dict):
                result[key] = merge(a[key], b.get(key, {}))
            else:
                result[key] = a[key]
    for key in b:
        if key.isalpha():
            if key in result:
                if isinstance(result[key], dict):
                    result[key] = merge(result[key], b[key])
                else:
                    result[key] = b[key]
            else:
                if isinstance(b[key], dict):
                    result[key] = merge({}, b[key])
                else:
                    result[key] = b[key]
    return result

