        elif instruction == 'down':
            new_row = min(len(map) - 1, path[-1][0] + 1)
        elif instruction == 'left':
            new_col = max(0, path[-1][1] - 1)
        elif instruction == 'right':
            new_col = min(len(map[0]) - 1, path[-1][1] + 1)

        if map[new_row][new_col] != 'X':
            path.append((new_row, new_col))
            if map[new_row][new_col] == 'E':
                break

    return path

