def alien_invasion(alien, humans):
    if not isinstance(alien, str) or not isinstance(humans, dict):
        return []
    continents = ["Asia", "Africa North", "Africa South", "Europe North", "Europe South"]
    encountered = []
    for continent in continents:
        for name, home in humans.items():
            if home == continent and name not in encountered:
                encountered.append(name)
    return encountered

def is_prime(num):
    if num < 2:
        return False
    for i in range(2, int(num**0.5) + 1):
        if num % i == 0:
            return False
    return True

